text:
/* Add these styles to your existing style.css */

/* Limit content height and add show more functionality */
.item-text {
  white-space: pre-wrap;
  word-break: break-word;
  font-size: var(--font-size);
  margin: 0;
  color: inherit;
  max-height: 120px; /* Limit to ~5 lines */
  overflow: hidden;
  position: relative;
  transition: max-height 0.3s ease;
}

.item-text.expanded {
  max-height: none; /* Remove limit when expanded */
}

/* Show more/less button */
.show-more-btn {
  background: rgba(46, 166, 255, 0.1);
  border: 1px solid rgba(46, 166, 255, 0.3);
  color: var(--accent);
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 11px;
  cursor: pointer;
  margin-top: 8px;
  display: inline-block;
  transition: all 0.2s ease;
}

.show-more-btn:hover {
  background: rgba(46, 166, 255, 0.2);
}

/* Gradient fade effect for truncated text */
.item-text::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 30px;
  background: linear-gradient(transparent, var(--bg2));
  opacity: 1;
  transition: opacity 0.3s ease;
  pointer-events: none;
}

.item-text.expanded::after {
  opacity: 0;
}

/* Ensure code blocks also have height limits */
.memory-item pre {
  max-height: 120px;
  overflow: hidden;
  position: relative;
  transition: max-height 0.3s ease;
}

.memory-item pre.expanded {
  max-height: none;
}

.memory-item pre::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 30px;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.35));
  opacity: 1;
  transition: opacity 0.3s ease;
  pointer-events: none;
}

.memory-item pre.expanded::after {
  opacity: 0;
}

/* Fullscreen content viewer */
#content-viewer {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.95);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  z-index: 10000;
  padding: 40px;
  box-sizing: border-box;
}

#content-viewer .content-container {
  background: var(--bg1);
  border-radius: 12px;
  padding: 30px;
  max-width: 90%;
  max-height: 90%;
  overflow: auto;
  position: relative;
  border: 1px solid rgba(46, 166, 255, 0.3);
}

#content-viewer .content-body {
  white-space: pre-wrap;
  word-break: break-word;
  font-size: 14px;
  line-height: 1.5;
  color: #e6eef6;
}

#content-viewer .close-btn {
  position: absolute;
  top: 15px;
  right: 15px;
  background: rgba(0, 0, 0, 0.7);
  border: none;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  font-size: 20px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}

#content-viewer .content-meta {
  margin-bottom: 20px;
  padding-bottom: 15px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  color: var(--muted);
  font-size: 12px;
}
